class PostsActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            AppTheme {
                PostsApp()
            }
        }
    }
}

@Composable
fun PostsApp() {
    RecruiterLayout(
        title = "Job Postings"
    ) { paddingValues ->
        PostsScreen(modifier = Modifier.padding(paddingValues))
    }
}

