@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun InterviewsScreen(
    modifier: Modifier = Modifier,
    scheduledInterviews: List<ScheduledInterview> = emptyList(),
    onRefresh: () -> Unit = {}
) {
    // Use passed interviews list
    val interviews = remember {
        mutableStateListOf<ScheduledInterview>().apply {
            addAll(scheduledInterviews)
        }
    }

    var searchText by remember { mutableStateOf("") }
    var selectedFilter by remember { mutableStateOf("all") }

    Column(
        modifier = modifier
            .fillMaxSize()
            .background(Color(0xFFF0F2F5))
    ) {
        // Header with title and description
        Column(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp)
        ) {
            Text(
                "Scheduled Interviews",
                fontWeight = FontWeight.Bold,
                fontSize = 24.sp,
                color = Color(0xFF1F2937)
            )
            Text(
                "Manage and track all your candidate interviews",
                fontSize = 14.sp,
                color = Color(0xFF9CA3AF)
            )
        }

        // Search bar
        OutlinedTextField(
            value = searchText,
            onValueChange = { searchText = it },
            placeholder = { Text("Search interviews...") },
            leadingIcon = { Icon(Icons.Default.Search, contentDescription = "Search") },
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 16.dp, vertical = 8.dp),
            shape = RoundedCornerShape(8.dp),
            colors = OutlinedTextFieldDefaults.colors(
                unfocusedBorderColor = Color(0xFFE5E7EB),
                focusedBorderColor = Color(0xFF3B82F6),
                unfocusedContainerColor = Color.White,
                focusedContainerColor = Color.White
            )
        )

        // Filter tabs
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 16.dp, vertical = 12.dp),
            horizontalArrangement = Arrangement.spacedBy(8.dp)
        ) {
            val scheduledCount = interviews.count { it.getStatus() == "scheduled" }
            val completedCount = interviews.count { it.getStatus() == "completed" }

            listOf(
                "all" to "All Interviews (${interviews.size})",
                "upcoming" to "Upcoming ($scheduledCount)",
                "completed" to "Completed ($completedCount)"
            ).forEach { (filter, label) ->
                Surface(
                    modifier = Modifier
                        .clickable { selectedFilter = filter },
                    color = if (selectedFilter == filter) Color(0xFFE0E7FF) else Color.White,
                    shape = RoundedCornerShape(6.dp),
                    border = if (selectedFilter == filter)
                        BorderStroke(1.dp, Color(0xFF3B82F6))
                    else
                        BorderStroke(1.dp, Color(0xFFE5E7EB))
                ) {
                    Text(
                        label,
                        modifier = Modifier.padding(horizontal = 12.dp, vertical = 8.dp),
                        fontSize = 12.sp,
                        fontWeight = FontWeight.Medium,
                        color = if (selectedFilter == filter) Color(0xFF3B82F6) else Color(0xFF6B7280)
                    )
                }
            }
        }

        // Interviews list
        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .padding(horizontal = 16.dp, vertical = 8.dp),
            verticalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            val filteredInterviews = when (selectedFilter) {
                "upcoming" -> interviews.filter { it.getStatus() == "scheduled" }
                "completed" -> interviews.filter { it.getStatus() == "completed" }
                else -> interviews
            }.filter { it.candidateName.contains(searchText, ignoreCase = true) }

            items(filteredInterviews) { interview ->
                InterviewItem(interview)
            }
        }
    }
}

@Composable
private fun InterviewItem(interview: ScheduledInterview) {
    val status = interview.getStatus()

    Card(
        modifier = Modifier.fillMaxWidth(),
        shape = RoundedCornerShape(12.dp),
        elevation = CardDefaults.cardElevation(defaultElevation = 1.dp),
        colors = CardDefaults.cardColors(containerColor = Color.White)
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            // Top section: Candidate info and status
            Row(
                modifier = Modifier.fillMaxWidth(),
                verticalAlignment = Alignment.Top,
                horizontalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                // Candidate image
                Surface(
                    modifier = Modifier
                        .size(48.dp)
                        .clip(CircleShape),
                    color = Color(0xFFE5E7EB)
                ) {
                    Image(
                        painter = painterResource(id = R.drawable.applicant),
                        contentDescription = interview.candidateName,
                        modifier = Modifier
                            .fillMaxSize()
                            .clip(CircleShape),
                        contentScale = ContentScale.Crop
                    )
                }

                // Candidate info
                Column(
                    modifier = Modifier
                        .weight(1f)
                        .fillMaxWidth()
                ) {
                    Text(
                        interview.candidateName,
                        fontWeight = FontWeight.Bold,
                        fontSize = 14.sp,
                        color = Color(0xFF1F2937)
                    )
                    Text(
                        interview.candidatePosition,
                        fontSize = 12.sp,
                        color = Color(0xFF6B7280),
                        lineHeight = 14.sp
                    )
                }

                // Status badge
                Surface(
                    color = when (status) {
                        "scheduled" -> Color(0xFFFEF3C7)
                        "completed" -> Color(0xFFDCFCE7)
                        else -> Color(0xFFF5F5F5)
                    },
                    shape = RoundedCornerShape(6.dp)
                ) {
                    Text(
                        when (status) {
                            "scheduled" -> "In Progress"
                            "completed" -> "Completed"
                            else -> "Pending"
                        },
                        fontSize = 11.sp,
                        fontWeight = FontWeight.Bold,
                        color = when (status) {
                            "scheduled" -> Color(0xFF92400E)
                            "completed" -> Color(0xFF065F46)
                            else -> Color(0xFF6B7280)
                        },
                        modifier = Modifier.padding(horizontal = 8.dp, vertical = 4.dp)
                    )
                }
            }

            Spacer(modifier = Modifier.height(12.dp))

            // Interview details
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.spacedBy(16.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                InterviewDetailItem(
                    icon = Icons.Default.CalendarToday,
                    text = interview.date,
                    modifier = Modifier.weight(1f)
                )
                InterviewDetailItem(
                    icon = Icons.Default.Schedule,
                    text = interview.time,
                    modifier = Modifier.weight(1f)
                )
            }

            Spacer(modifier = Modifier.height(8.dp))

            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.spacedBy(16.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                InterviewDetailItem(
                    icon = Icons.Default.LocationOn,
                    text = interview.interviewType,
                    modifier = Modifier.weight(1f)
                )
            }
        }
    }
}

@Composable
private fun InterviewDetailItem(
    icon: androidx.compose.ui.graphics.vector.ImageVector,
    text: String,
    modifier: Modifier = Modifier
) {
    Row(
        modifier = modifier,
        verticalAlignment = Alignment.CenterVertically,
        horizontalArrangement = Arrangement.spacedBy(6.dp)
    ) {
        Icon(
            imageVector = icon,
            contentDescription = text,
            modifier = Modifier.size(14.dp),
            tint = Color(0xFF6B7280)
        )
        Text(
            text,
            fontSize = 12.sp,
            color = Color(0xFF6B7280)
        )
    }
}

package com.example.jobify.ui.layout

import android.content.Intent
import androidx.compose.foundation.layout.PaddingValues
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Menu
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.runtime.rememberCoroutineScope
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalContext
import com.example.jobify.InterviewsActivity
import com.example.jobify.LoginActivity
import com.example.jobify.PostsActivity
import com.example.jobify.RecruiterProfileActivity
import com.example.jobify.ui.drawer.RecruiterDrawerContent
import kotlinx.coroutines.launch

/**
 * Wrapper layout for all recruiter pages with navigation drawer
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun RecruiterLayout(
    title: String,
    content: @Composable (PaddingValues) -> Unit
) {
    val drawerState = rememberDrawerState(initialValue = DrawerValue.Closed)
    val scope = rememberCoroutineScope()
    val context = LocalContext.current

    ModalNavigationDrawer(
        drawerState = drawerState,
        drawerContent = {
            ModalDrawerSheet {
                RecruiterDrawerContent(
                    onPublishJobClick = {
                        context.startActivity(Intent(context, PostsActivity::class.java))
                        scope.launch { drawerState.close() }
                    },
                    onInterviewsClick = {
                        context.startActivity(Intent(context, InterviewsActivity::class.java))
                        scope.launch { drawerState.close() }
                    },
                    onSettingsClick = {
                        context.startActivity(Intent(context, RecruiterProfileActivity::class.java))
                        scope.launch { drawerState.close() }
                    },
                    onLogoutClick = {
                        val intent = Intent(context, LoginActivity::class.java)
                        intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
                        context.startActivity(intent)
                    }
                )
            }
        }
    ) {
        Scaffold(
            containerColor = Color(0xFFF0F2F5),
            topBar = {
                TopAppBar(
                    title = { Text(title) },
                    navigationIcon = {
                        IconButton(onClick = { scope.launch { drawerState.open() } }) {
                            Icon(Icons.Filled.Menu, contentDescription = "Menu")
                        }
                    }
                )
            }
        ) { paddingValues ->
            content(paddingValues)
        }
    }
}

